const API_URL="localhost"===window.location.hostname||"127.0.0.1"===window.location.hostname?"http://localhost:3000/api":`http://${window.location.hostname}:3000/api`;async function chargerDonneesTendance(){const e=document.body.dataset.articleId;if(!e)return console.error("‚ùå ID de l'article non trouv√©"),void afficherErreurArticle("Article non identifi√©");try{const n=await fetch(`${API_URL}/fiche-tendance/data/${e}`),t=await n.json();if(t.success&&t.data){afficherDonneesArticle(t.data)}else console.error("‚ùå Erreur API:",t.message),afficherErreurArticle("Article non trouv√©")}catch(e){console.error("‚ùå Erreur connexion:",e),afficherErreurArticle("Erreur de connexion √† la base de donn√©es")}}function afficherDonneesArticle(e){const n=document.querySelector(".article-title");n&&(n.textContent=e.titre);const t=document.getElementById("badge-hot");t&&e.hot&&(t.innerHTML='\n            <div style="background: linear-gradient(45deg, #ff6b6b, #ff8e53); \n                        padding: 10px; margin-bottom: 20px; text-align: center; \n                        border-radius: 8px; font-weight: bold; color: white;">\n                üî• Tendance HOT en ce moment !\n            </div>\n        ');const o=document.querySelector(".publication-date");if(o&&e.date_publication){const n=new Date(e.date_publication).toLocaleDateString("fr-FR",{year:"numeric",month:"long",day:"numeric"});o.textContent=`Publi√© le ${n}`}const r=document.querySelector(".entete img");if(r&&e.categorie){const n={"PC Gaming":"gaming.png",Console:"gaming.png",Smartphone:"mobile.png",Tablette:"mobile.png",Camera:"camera.png","Casque VR":"vr.png"}[e.categorie]||"gaming.png";r.src=`../../../frontend/public/assets/images/${n}`}const i=document.getElementById("article-content");if(i)if(e.sections&&e.sections.length>0){let n="";e.sections.forEach(e=>{n+=`\n                    <h2 style="color: #667eea; margin-top: 30px; margin-bottom: 15px;">${e.titre}</h2>\n                    <p style="line-height: 1.8; white-space: pre-line;">${e.contenu}</p>\n                `}),i.innerHTML=n}else i.innerHTML=`\n                <p style="line-height: 1.8; margin-bottom: 20px;">${e.resume||"Contenu en cours de r√©daction..."}</p>\n            `;console.log("‚úÖ Article charg√©:",e.titre)}function afficherErreurArticle(e){const n=document.getElementById("article-content");n&&(n.innerHTML=`\n            <div style="background: #fee; border: 2px solid #fcc; padding: 20px; border-radius: 8px; text-align: center;">\n                <p style="color: #c00; font-weight: bold;">‚ö†Ô∏è ${e}</p>\n            </div>\n        `)}document.addEventListener("DOMContentLoaded",chargerDonneesTendance);